import React, { useEffect } from "react"
import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
// import PlacesAutocomplete from 'react-places-autocomplete'
// import Autocomplete from "react-google-autocomplete";
// import GooglePlacesAutocomplete from 'react-google-places-autocomplete';
// import ReactStreetview from 'react-streetview';

// import Streetview from 'react-google-streetview';

import PlacesAutocomplete, {
    geocodeByAddress,
    getLatLng,
} from 'react-places-autocomplete';
import scriptLoader from 'react-async-script-loader'


function AutoSearch(props, isScriptLoaded, isScriptLoadSucceed) {
    // console.log(props)
    var dataFromAPI;

    let navigate = useNavigate()

    const [address, setAddress] = useState("")

    const [lat, setLat] = useState('')
    const [lng, setLng] = useState('')

    const [userInput, setUserInput] = useState("")

    const [coordinates, setCoordinates] = useState({
        lat: null,
        lng: null,
    });

    useEffect(() => {
        // props.updateAddress(address)
        // props.updateCoordinates(coordinates)
    }, [])

    const handleChange = (value) => {
        setUserInput(value)
    }

    // const handleSelect = (value) => {
    //     console.log('handle select', address)
    //     setAddress(value)
    //     props.updateAddress(address)
    //     localStorage.setItem("address", value)
    //     // navigate('/apartment-view')

    //     geocodeByAddress(value)
    //         .then(results => getLatLng(results[0]))
    //         .then(latLng => console.log('Success', latLng))
    // .then(({ lat, lng }) => {
    //      NProgress.done()
    //      setCoordinates({ lat : lat, lng : lng })
    //         .catch(error => console.error('Error', error))
    //     ;
    // }

    // const setStates = async (latLng) => {
    //     setLat(latLng.lat)
    //     setLng(latLng.lng)
    //     console.log('my lat object', lat)
    //     console.log('my lng object', lng)
    // }
    const setCoordinatesFunction = (dataFromAPI) => {
        setCoordinates({coordinates : { lat: dataFromAPI.lat, lng: dataFromAPI.lng}});
        console.log('my coordinates!!!!!', coordinates);
        console.log('dataFromAPI', dataFromAPI);
    }

    const handleSelect = async (value) => {

        const results = await geocodeByAddress(value);
        const latLng = await getLatLng(results[0]);

        geocodeByAddress(value)
            .then(results => getLatLng(results[0]))
            .then(latLng => {
                console.log('Success', latLng);
                dataFromAPI = latLng;
                setCoordinatesFunction(dataFromAPI);
            })
            .catch(error => console.error('Error', error))

        setCoordinates({ latLng })
        // setLat(latLng.lat)
        // setLng(latLng.lng)

        console.log('object from api', typeof (latLng))
        // console.log("object from api's lat", latLng.lat)
        // console.log("object from api's lng", latLng.lng)
        console.log('my lat object', lat)
        console.log('my lng object', lng)
        console.log('my coordinates object', coordinates)

        setUserInput(value)
        setAddress(value)

        props.updateAddress(address)
        // props.updateCoordinates(coordinates)

        localStorage.setItem("address", value)
        localStorage.setItem("coords", coordinates)

        navigate('/apartment-view')
    }

    // example lat was 46.9171876
    // example lng was 17.8951832
    // const streetViewPanoramaOptions = {
    //     position: { lat: coordinates.lat, lng: coordinates.lng },
    //     pov: { heading: 100, pitch: 0 },
    //     zoom: 1
    // };

    if (props.isScriptLoaded && props.isScriptLoadSucceed) {
        return (
            <div>


                {/* <div style={{
                    width: '800px',
                    height: '450px',
                    backgroundColor: '#eeeeee'
                }}>
                    <Streetview
                        apiKey={process.env.REACT_APP_GOOGLE_MAP_API}
                        streetViewPanoramaOptions={streetViewPanoramaOptions}
                    />
                </div> */}


                <PlacesAutocomplete value={userInput} onChange={handleChange} onSelect={handleSelect}>
                    {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
                        <div>
                            <input {...getInputProps({
                                placeholder: "Enter Address ...",
                                className: 'location-search-input'
                            })} />

                            <div className="autocomplete-dropdown-container">
                                {loading && <div>Loading...</div>}

                                {suggestions.map((suggestion, key) => {
                                    const style = suggestion.active ?
                                        { backgroundColor: "#4287f5", cursor: "pointer" } :
                                        { backgroundColor: "#ffffff", cursor: "pointer" }
                                    key = suggestion.description;

                                    return (
                                        <div key={suggestion.description} {...getSuggestionItemProps(suggestion, { style })}>
                                            <span>{suggestion.description}</span>
                                        </div>
                                    )
                                })

                                }
                            </div>
                        </div>
                    )}
                </PlacesAutocomplete>

            </div>
        )
    } else {
        return (
            <div>NOT loaded</div>
        )
    }
}
export default scriptLoader([`https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_GOOGLE_MAP_API}&libraries=places`])(AutoSearch);
